package ch.bbw.zork;/** * Author:  Michael Kolling, Version: 1.1, Date: August 2000 * refactoring: Rinaldo Lanza, September 2020 */import ch.bbw.objects.Crowbar;import ch.bbw.objects.Key;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.stream.Collectors;public class Room {		private String description;	private HashMap<String, Room> exits;	private Key key;	private Crowbar crowbar;	public Room(String description, Key key, Crowbar crowbar) {		this.description = description;		this.exits = new HashMap<>();		this.key = key;		this.crowbar = crowbar;	}	public void setExits(Room north, Room east, Room south, Room west) {		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);	}	public String shortDescription() {		return description;	}	public String longDescription() {		StringBuilder stringBuilder = new StringBuilder("You are in " + description + ".\n");		stringBuilder.append(exitString());		return stringBuilder.toString();	}	private String exitString() {		return "Exits:" + String.join(" ", exits.keySet());	}	public Room nextRoom(String direction) {		return exits.get(direction);	}	public String getDescription() {		return description;	}	public void setDescription(String description) {		this.description = description;	}	public HashMap<String, Room> getExits() {		return exits;	}	public void setExits(HashMap<String, Room> exits) {		this.exits = exits;	}	public Key getKey() {		return key;	}	public void setKey(Key key) {		this.key = key;	}	public Crowbar getCrowbar() {		return crowbar;	}	public void setCrowbar(Crowbar crowbar) {		this.crowbar = crowbar;	}}