package ch.bbw.zork;/** * Author:  Michael Kolling, Version: 1.1, Date: August 2000 * refactoring: Rinaldo Lanza, September 2020 */import ch.bbw.objects.Item;import java.util.ArrayList;import java.util.HashMap;public class Room {  private String description;  private HashMap<String, Room> exits;  private final ArrayList<Item> items = new ArrayList<>();  public Room(String description) {    this.description = description;    this.exits = new HashMap<>();  }  public void setExits(Room north, Room east, Room south, Room west) {    exits.put("north", north);    exits.put("east", east);    exits.put("south", south);    exits.put("west", west);  }  public String longDescription() {    StringBuilder stringBuilder = new StringBuilder("You are in " + description + ".\n");    stringBuilder.append(exitString());    return stringBuilder.toString();  }  private String exitString() {    return "Exits:" + String.join(" ", exits.keySet());  }  public Room nextRoom(String direction) {    return exits.get(direction);  }  public String getDescription() {    return description;  }  public void setDescription(String description) {    this.description = description;  }  public HashMap<String, Room> getExits() {    return exits;  }  public void setExits(HashMap<String, Room> exits) {    this.exits = exits;  }  public ArrayList<Item> getItems() {    return items;  }}